import React from 'react'
import classNames from 'classnames'

import removeBtn from '../assets/img/remove.svg'

const List = ({items, isRemovable, onClick}) =>{

    const removeList = (item) => {
        if (window.confirm('Are you sure you wanna delete this whole list?')){
            onRemove(item)
        }
    }
    return (
        <ul onClick={onClick} className="todo__list" >
            {
               items.map((i, index) => ( <li key={index} className={classNames(i.className, {'active': i.active} )}>
                {i.icon ?  <i>{i.icon}</i> : <i className={classNames('circle', i.color)}></i>} 
                <span>{i.name}</span>
                {isRemovable ? <button className="list__remove-icon"  onClick={()=>removeList()}>  
                                <img src={removeBtn} alt="Remove the list button"/>
                               </button> :""} 
                </li>
                ))
            }
        </ul> 
    );
};

export default List;
