import React, {useState, useEffect} from 'react'; 
import axios from 'axios'

import AddList from './components/AddList'
import List from './components/List'
import Tasks from './components/Tasks'

import listSvg from  './assets/img/list.svg'
import doIt from  './assets/img/do-it.png'
import './css/reset.css';
import './css/index.min.css';
import './css/List.min.css';
import './css/AddList.min.css';
import './css/Tasks.min.css';

function App() {

  const [lists, setLists] = useState(null)
  const [colors, setColors] = useState(null)
  const [activeItem, setActiveItem] = useState(null)
  useEffect(() => {
    axios.get('http://localhost:3001/lists?_expand=color&_embed=tasks').then(({data}) => {
      setLists(data)
    });
    axios.get('http://localhost:3001/colors').then(({data}) => {
      setColors(data)
    });
  }, []);   


  const onAddList = (obj) =>{
    setLists([...lists, obj])
  }

  const onEditTitle = (id, newTitle) => {
    const newList = lists.map((i) => {
      if (i.id===id){
        i.name = newTitle;
        return i
      }
    });
    console.log (newList )
    axios.post('http://localhost:3001/lists', {
      id: id,
      name: newTitle, 
      colorId: lists.find(item => item.id === id).colorId,
      color: lists.find(item => item.id === id).color
    })
    .then(({data}) => {
        setLists(data)
    })
  }

  return (
    <div className="todo">
      <section className="todo__sidebar">
        <List items= {[
          { 
            icon: <img src={listSvg} alt="list icon"/>,
            name:'All tasks',
            active: true 
           }
        ]}
        isRemovable={false}/>
        {lists?(
        <List items= {lists} 
        onRemove = {(id)=>
          setLists(lists.filter(item => item.id !== id))}
        onClickItem={(i) => {
          i.active=true
          setActiveItem(i)}}
        activeItem={activeItem}
        isRemovable/>
        ) : <div style={{padding: 10 + 'px'}}>Loading...</div>}
         <AddList onAdd={onAddList} colors={colors}/>
      </section>
      <section className="todo__tasks">
        {activeItem?<Tasks onEditTitle={onEditTitle} list={activeItem}/>:""}
        <img className ="shai"src={doIt} alt="list icon"/>
      </section>
    </div>
  );
}

export default App;
