import React, {useState} from 'react'
import axios from 'axios'

import AddTask from './AddTask'
import Task from './Task'
import Edit from '../assets/img/edit.svg';
import Remove from '../assets/img/remove.svg';

function Tasks({lists, onEditTitle, onAddTask, onCompleteTask, onDeleteTask, onUpdateTask}) {

    const editTitle = (list) => {
        let newTitle = window.prompt('Write new list title', list.name)
        if (newTitle){
            // changing state
            onEditTitle(list.id, newTitle)
            // changing db
            axios.patch('http://localhost:3001/lists/' + list.id, {
                name: newTitle
            }).catch(()=>{
                alert('Database error :((')
            })
        } else{
            return;
        }
    }
    const checkTask = (task) => {
        // changing state
        //onCompleteTask(task.id, task.listId)
        // changing db
        const data = new FormData();
        data.append("json", JSON.stringify({"1":"xyxyxyxyx", "2":"hhbhbhbh"}));
        console.log(data)

        fetch('/tasks',{
            method: "POST",
            body: data
            }).catch(()=>{
                alert('Database error :((')
            })

        //axios.patch('http://localhost:3001/tasks/' + task.id, {
          //      completed: (task.completed)
            //}).catch(()=>{
              //  alert('Database error :((')
            //})
    }
    return (
        lists.map(list=>(
        <div key={list.id} className="tasks">
                <h2 className={"tasks__title " + list.color.name}>
                    {list.name}
                    <img onClick={()=>editTitle(list)} src={Edit} alt="Edit icon"/> 
                </h2>
                <ul className="tasks__items">
                {list.tasks&&list.tasks.length > 0 ? (list.tasks.map((i) => (
                    <Task 
                    key={i.id} 
                    task={i} 
                    checkTask={checkTask}
                    onDeleteTask ={onDeleteTask}
                    onUpdateTask={onUpdateTask}
                    Edit = {Edit}
                    Remove={Remove}
                    />
                    ))) :
                    <h2>No tasks</h2>} 
                </ul>
                <AddTask 
                list = {list}  
                onAddTask={onAddTask}
                />
        </div>
        ))
    )
}

export default Tasks;
