import React, {useState} from 'react'
import axios from 'axios'
import classNames from 'classnames'

export default function Task({task, checkTask, onDeleteTask, Remove, Edit}) {

    const [taskText, setTaskText] = useState(task.text)

    const updateTask = () => {
        axios.patch('http://localhost:3001/tasks/'+task.id, {
            text: taskText
        }).catch(()=>{
            alert('Database error :((')
        });
    }

    const deleteTask = (taskId, listId) => {
        onDeleteTask(taskId, listId)
        axios.delete('http://localhost:3001/tasks/'+task.id).then(()=>{
                //onRemove(task.id, listId)
            }).catch(()=>{
                alert('Database error :((')
            });
    }

    return (
        <li key={task.id} className="tasks__item">
                    <div className="checkbox">
                    <input 
                    id={"check-"+ task.id} 
                    type="checkbox" 
                    checked={task.completed}
                    onChange={()=>checkTask(task)}
                    />
                    <label 
                    htmlFor={"check-"+task.id} 
                    >
                        <svg width="11" height="8" viewBox="0 0 11 8" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M9.29999 1.20001L3.79999 6.70001L1.29999 4.20001" stroke="white" 
                            strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                    </label>
                    </div> 
                    <input type="text" value={taskText} onChange={event=>setTaskText(event.target.value)}/>
                    <img onClick={()=>updateTask(task.id, task.listId)} src={Edit} alt="Edit icon"/> 
                    <img onClick={()=>deleteTask(task.id, task.listId)} src={Remove} alt="Remove icon"/> 

                </li> 
    )
}
