import React, {useState}  from 'react'
import List from './List'

const AddList  = () =>{
    const [popUp , showPopUp] = useState(false  ) 
    return <div  className= 'add-list'  >  
        <List onClick={() => showPopUp(!popUp)} items= {[
            {
            className: 'add-list__button',
            icon: <svg width="12" height="12" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M8 1V15" stroke="#eeeeee" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
            <path d="M1 8H15" stroke="#eeeeee" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
            ,
            name:'Add list'
            }
        ]}isRemovable={false}/>
        {popUp && <div className="add-list__popup">
            <input className="Field" type="text"/>
            <h1>123</h1 ></div>}
    </div> 
}

export default AddList;
