import React, {useState, useEffect} from 'react'; 
import axios from 'axios'

import AddList from './components/AddList'
import List from './components/List'
import Tasks from './components/Tasks'

import listSvg from  './assets/img/list.svg'
import doIt from  './assets/img/do-it.png'
import DB from  './assets/db.json'

import './css/reset.css';
import './css/index.min.css';
import './css/List.min.css';
import './css/AddList.min.css';
import './css/Tasks.min.css';

function App() {

  const [lists, setLists] = useState(null)
  const [colors, setColors] = useState(null)
  console.log(items)
  useEffect(() => {
    axios.get('http://localhost:3001/lists?_expand=color').then( ({data}) => {
      setLists(data);
    });
    axios.get('http://localhost:3001/colors').then( ({data}) => {
      setColors(data);
    });
  }, []); 


  const onAddList = (obj) =>{
    obj.color = colors.find(color => color.id === obj.colorId).name
    setLists([...lists, obj])
  }

  return (
    <div className="todo">
      <section className="todo__sidebar">
        <List items= {[
          { 
            icon: <img src={listSvg} alt="list icon"/>,
            name:'All tasks',
            active: true 
           }
        ]}
        isRemovable={false}/>
        <List items= {lists} 
        onRemove = {(i)=>alert(i)}
        isRemovable/>
         <AddList onAdd={onAddList} colors={colors}/>
      </section>
      <section className="todo__tasks">
        <Tasks/>
        <img className ="shai"src={doIt} alt="list icon"/>
      </section>
    </div>
  );
}

export default App;
