import React, {useState} from 'react'
import axios from 'axios'

import classNames from 'classnames'
import AddTask from './AddTask'
import Task from './Task'
import Edit from '../assets/img/edit.svg';

function Tasks({list, onEditTitle, onAddTask, onCompleteTask}) {


    const editTitle = () => {
        let newTitle = window.prompt('Write new list title', list.name)

        if (newTitle){
            console.log(newTitle.lenght)
            // changing state
            onEditTitle(list.id, newTitle)
            // changing db
            axios.patch('http://localhost:3001/lists/' + list.id, {
                name: newTitle
            }).catch(()=>{
                alert('Database error :((')
            })
        } else{
            return
        }
    }
    const checkTask = (task) => {
        // changing state
        onCompleteTask(task.id, task.listId)
        // changing db
        axios.patch('http://localhost:3001/tasks/' + task.id, {
                completed: (task.completed)
            }).catch(()=>{
                alert('Database error :((')
            })
    }
    return (
        <div className="tasks">
            <h2 className={"tasks__title " + list.color.name}>{list.name}
                <img onClick={editTitle} src={Edit} alt="Edit icon"/> 
            </h2>
            <ul className="tasks__items">
            {list.tasks&&list.tasks.length > 0 ? (list.tasks.map((i) => (
                < Task task={i} />
                ))) :
                <h2>No tasks</h2>} 
            </ul>
            <AddTask list = {list}  onAddTask={onAddTask}/>
        </div>
    )
}

export default Tasks;
