import React, {useState} from 'react'
import axios from 'axios'

import classNames from 'classnames'
import AddTask from './AddTask'
import Edit from '../assets/img/edit.svg';

function Tasks({list, onEditTitle, onAddTask}) {


    const editTitle = () => {
        let newTitle = window.prompt('Write new list title', list.name)
        if (newTitle){
            // changing state
            onEditTitle(list.id, newTitle)
            // changing db
            axios.patch('http://localhost:3001/lists/' + list.id, {
                name: newTitle
            }).catch(()=>{
                alert('Database error :((')
            })
        } else {
            alert("Empty title isn't allowed, don't brake the law!")
        }
    }

    return (
        <div className="tasks">
            <h2 className={"tasks__title " + list.color.name}>{list.name}
                <img onClick={editTitle} src={Edit} alt="Edit icon"/> 
            </h2>
            <ul className="tasks__items">
            {list.tasks&&list.tasks.length > 0 ? (list.tasks.map((i) => (
                <li key={i.id} className="tasks__item">
                    <div className="checkbox">
                    <input 
                    id={"check-"+ i.id} 
                    type="checkbox" 
                    checked={i.completed}
                    onClick={()=>i.completed=true}
                    />
                    <label 
                    htmlFor={"check-"+i.id} 
                    >
                        <svg width="11" height="8" viewBox="0 0 11 8" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M9.29999 1.20001L3.79999 6.70001L1.29999 4.20001" stroke="white" 
                            strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                    </label>
                    <p className = {classNames({'line-through':i.completed})}>
                        {i.text}
                    </p>
                    </div> 
                </li> 
                ))) :
                <h2>No tasks</h2>} 
            </ul>
            <AddTask list = {list}  onAddTask={onAddTask}/>
        </div>
    )
}

export default Tasks;
