import React, {useState} from 'react';
import logo from './logo.svg';
import AddList from './components/AddList'
import List from './components/List'
import listSvg from  './assets/img/list.svg'

import doIt from  './assets/img/do-it.png'
import DB from  './assets/db.json'

import './css/reset.css';
import './css/index.min.css';
import './css/List.min.css';
import './css/AddList.min.css';
import './css/Tasks.min.css';

function App() {
  const [lists, setLists] = useState(DB.lists.map((i)=>{
    i.color = DB.colors.find(color => color.id === i.colorId).name
    return i}))
  const onAddList = (obj) =>{
    obj.color = DB.colors.find(color => color.id === obj.colorId).name
    setLists([...lists, obj])
  }
  return (
    <div className="todo">
      <section className="todo__sidebar">
        <List items= {[
          { 
            icon: <img src={listSvg} alt="list icon"/>,
            name:'All tasks',
            active: true 
           }
        ]}
        isRemovable={false}/>
        <List items= {lists} 
        onRemove = {(i)=>alert(i)}
        isRemovable/>
         <AddList onAdd={onAddList} colors={DB.colors}/>
      </section>
      <section className="todo__tasks">
        <div className="tasks">
          <h2 className="tasks__title pink">Фронтэнд</h2>
        </div>
        <img className ="shai"src={doIt} alt="list icon"/>
      </section>
    </div>
  );
}

export default App;
