import React from 'react'
import '../css/List.min.css';
import classNames from 'classnames'

const List = ({items}) =>{
    return (
        <ul className="todo__list">
            {
               items.map((i, index) => ( <li key={index} className={i.active?"active":""}>
                <i>{i.icon ? i.icon : <i className={'circle circle--'+i.color}></i>}</i> 
                <span>{i.name}</span>
                </li>))
            }
        </ul> 
    );
};

export default List;
