import React, {useState} from 'react';
import logo from './logo.svg';
import AddList from './components/AddList'
import List from './components/List'
import listSvg from  './assets/img/list.svg'

import doIt from  './assets/img/do-it.png'
import DB from  './assets/db.json'

import './css/reset.css';
import './css/index.min.css';
import './css/List.min.css';
import './css/AddList.min.css';

function App() {
  const [lists, setLests] = useState(DB.lists.map((i)=>{
    i.color = DB.colors.find(color => color.id === i.colorId).name
    return i}))
  return (
    <div className="todo">
      <section className="todo__sidebar">
        <List items= {[
          { 
            icon: <img src={listSvg} alt="list icon"/>,
            name:'All tasks',
            active: true 
           }
        ]}
        isRemovable={false}/>
        <List items= {lists}
        isRemovable/>
         <AddList colors={DB.colors}/>
      </section>
      <section className="todo__tasks">
      <img className ="shai"src={doIt} alt="list icon"/>
      </section>
    </div>
  );
}

export default App;
