import React from 'react'
import classNames from 'classnames'
import axios from 'axios'

import removeBtn from '../assets/img/remove.svg'

const List = ({items, isRemovable, onClick, onRemove}) =>{
    const removeList = (i) => {
        if (window.confirm('Are you sure you wanna delete this whole list?')){
            axios.delete('http://localhost:3001/lists');
            onRemove(i)
        }
    }

    return (
        <ul onClick={onClick} className="todo__list" >
            {  
                items?(items.map((i, index) => ( <li key={index} className={classNames(i.className, {'active': i.active} )}>
                {i.icon ?  <div className='i'>{i.icon}</div> : <div className={classNames('circle', i.color.name, 'i')}></div>} 
                <span>{i.name}</span>
                {isRemovable ? <button className="list__remove-icon"  onClick={()=>removeList(i)}>  
                                <img src={removeBtn} alt="Remove the list button"/>
                               </button> :""} 
                </li>
                ))):""
            }
        </ul> 
    );
};

export default List;
