import React, {useState, useEffect}  from 'react'
import axios from 'axios'

export default function AddTask() {
    const [popUp, showPopUp] = useState(true);

    const addTask = () => {
        showPopUp(false)
    }

    return (
        <div className="add-task">

           { !popUp ? (
            <div onClick={(()=>{showPopUp(true)})} className="add-task__title"> + Add new task</div>
           ) : (
            <div action={addTask} className="add-taask__popup">
                <input className="field" type="text" placeholder="Task name"/>
                <button type="submit" >Submit</button>
            </div>) 
            }       
        </div>
    )
}
