import React from 'react'
import axios from 'axios'
import classNames from 'classnames'

export default function Task({task, checkTask, onDeleteTask}) {

    const editTask = () => {

    }

    const deleteTask = () => {
        console.log(task)
        onDeleteTask(task.id)
        axios.delete('http://localhost:3001/tasks/'+task.id).then(()=>{
               // onRemove(i.id)
            }).catch(()=>{
                alert('Database error :((')
            });
    }

    return (
        <li key={task.id} className="tasks__item">
                    <div className="checkbox">
                    <input 
                    id={"check-"+ task.id} 
                    type="checkbox" 
                    checked={task.completed}
                    onChange={()=>checkTask(task)}
                    />
                    <label 
                    htmlFor={"check-"+task.id} 
                    >
                        <svg width="11" height="8" viewBox="0 0 11 8" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M9.29999 1.20001L3.79999 6.70001L1.29999 4.20001" stroke="white" 
                            strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                    </label>
                    <p onClick={deleteTask} className = {classNames({'line-through':task.completed})}>
                        {task.text}
                    </p>
                    </div> 
                </li> 
    )
}
